INSERT INTO `wc_postmeta` VALUES (1,2,'_wp_page_template','default'),(782,63,'tix_options','a:17:{s:8:\"currency\";s:3:\"USD\";s:10:\"event_name\";s:21:\"WordCamp Seattle 2014\";s:7:\"version\";i:20180709;s:20:\"reservations_enabled\";b:0;s:15:\"refunds_enabled\";b:0;s:18:\"refund_all_enabled\";b:0;s:8:\"archived\";b:0;s:15:\"payment_methods\";a:1:{s:6:\"paypal\";b:1;}s:30:\"email_template_single_purchase\";s:233:\"Hi there!\n\nYou have purchased the following ticket:\n\n[receipt]\n\nYou can edit the information for the purchased ticket at any time before the event, by visiting the following link:\n\n[ticket_url]\n\nLet us know if you have any questions!\";s:32:\"email_template_multiple_purchase\";s:237:\"Hi there!\n\nThank you so much for purchasing a ticket and hope to see you soon at our event. You can edit your information at any time before the event, by visiting the following link:\n\n[ticket_url]\n\nLet us know if you have any questions!\";s:40:\"email_template_multiple_purchase_receipt\";s:239:\"Hi there!\n\nYou have purchased the following tickets:\n\n[receipt]\n\nYou can edit the information for all the purchased tickets at any time before the event, by visiting the following link:\n\n[ticket_url]\n\nLet us know if you have any questions!\";s:32:\"email_template_pending_succeeded\";s:237:\"Hey there!\n\nYour payment for [event_name] has been completed, looking forward to seeing you at the event! You can access and change your tickets information by visiting the following link:\n\n[ticket_url]\n\nLet us know if you need any help!\";s:29:\"email_template_pending_failed\";s:293:\"Hey there!\n\nWe\'re so sorry, but it looks like your payment for [event_name] has failed! Please check your payment transactions for more details. If you still wish to attend the event, feel free to purchase a new ticket using the following link:\n\n[ticket_url]\n\nLet us know if you need any help!\";s:28:\"email_template_single_refund\";s:230:\"Hey there!\n\nYour refund for [event_name] has been completed. If you change your mind and still wish to attend the event, feel free to purchase a new ticket using the following link:\n\n[ticket_url]\n\nLet us know if you need any help!\";s:30:\"email_template_multiple_refund\";s:229:\"Hey there!\n\nYour ticket for [event_name] has been refunded. If you change your mind and still wish to attend the event, feel free to purchase a new ticket using the following link:\n\n[ticket_url]\n\nLet us know if you need any help!\";s:22:\"payment_options_stripe\";a:1:{s:10:\"api_predef\";s:12:\"wpcs-sandbox\";}s:22:\"payment_options_paypal\";a:1:{s:10:\"api_predef\";s:16:\"wordcamp-sandbox\";}}'),(783,63,'tix_home_url','https://2014.seattle.wordcamp.test'),(784,63,'tix_admin_url','https://2014.seattle.wordcamp.test/wp-admin/'),(785,63,'tix_stats_sold','0'),(786,63,'tix_stats_remaining','700'),(787,63,'tix_stats_subtotal','0'),(788,63,'tix_stats_discounted','0'),(789,63,'tix_stats_revenue','0'),(790,63,'tix_stats_last_modified','1403381449'),(791,63,'tix_earliest_start','1399334400'),(792,63,'tix_undefined_start',''),(793,63,'tix_latest_end','1626331439'),(794,63,'tix_undefined_end','1'),(795,63,'tix_archived','0');
